    setSelectedFilm(film);
  };

  return (
    <div>
      <h1>Top Films</h1>
      {/* Map over the topFilms array and render film information */}
      {topFilms.map((film, index) => (
        <div key={index}>
          <table>
            <tr>
            <th>{film.title}</th>
            </tr>
            <tr>
              <td></td>
            </tr>
          </table>
        </div>
      ))}
    </div>
  );
}

export default App;
