import React, { useState, useEffect } from 'react';
import axios from 'axios';


function Movies() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
      try {
          const response = await axios.get(`/search?query=${query}`);
          setResults(response.data);
      } catch (error) {
          console.error('Error searching movies:', error);
      }
  };

  return (
      <div>
        <br/>
        <section>
            <input
                type="movie_search"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                      handleSearch();
                  }
                }}
                placeholder="Search movies by title, actor, or genre..."
            />
          </section>
          
        <table class="styled-table">
        <thead>
            <tr>
                <td>Title</td>
                <td>Actors</td>
                <td>Categories</td>
                
            </tr>
        </thead>
        <tbody>              
          {results.map(result => (
                  <li key={result.film_id}>
                      <td>{result.title}</td>
                      <td>Description: {result.description}</td>
                      <td>Release Year: {result.release_year}</td>
                      <td>Rental Rate: ${result.rental_rate.toFixed(2)}</td>
                      <td>Length: {result.length} minutes</td>
                      <td>Actors: {result.actors.map(actor => `${actor.first_name} ${actor.last_name}`).join(', ')}</td>
                      <td>Genres: {result.categories.join(', ')}</td>
                  </li>
              ))}
        </tbody>
        </table>
      </div>
  );
}

  
  export default Movies;
