import React, { useState, useEffect } from 'react';
import axios from 'axios';


function Movies() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
      try {
          const response = await axios.get(`/search?query=${query}`);
          setResults(response.data);
      } catch (error) {
          console.error('Error searching movies:', error);
      }
  };

  return (
      <div>
          <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search movies by title, actor, or genre..."
          />
          <button onClick={handleSearch}>Search</button>
          <ul>
              {results.map(result => (
                  <li key={result.film_id}>
                      <h3>{result.title}</h3>
                      <p>Description: {result.description}</p>
                      <p>Release Year: {result.release_year}</p>
                      <p>Rental Rate: ${result.rental_rate.toFixed(2)}</p>
                      <p>Length: {result.length} minutes</p>
                      <p>Actors: {result.actors.map(actor => `${actor.first_name} ${actor.last_name}`).join(', ')}</p>
                      <p>Genres: {result.categories.join(', ')}</p>
                  </li>
              ))}
          </ul>
      </div>
  );
}

  
  export default Movies;
